def topTwo(nums):
    # Byg et frekvenskort
    frequency_map = {}
    for num i nums:
        if num in frequency_map:
            frequency_map[num] += 1
        else:
            frequency_map[num] = 1

    # Find de to mest forekommende elementer
    most_frequent = sorted(frequency_map.items(), key=lambda x: x[1], reverse=True)[:2]

    return [num for num, _ in most_frequent]
