def find_common_elements(arr1, arr2):
    common_elements = []
    for i in range(len(arr1)):
        for j in range(len(arr2)):
            if arr1[i] == arr2[j]:
                # Kontrollera om elementet redan finns i listan common_elements
                om arr1[i] inte finns i common_elements:
                    common_elements.append(arr1[i])
    returnera common_elements
